        <h3>Danh s√°ch ng∆∞·ªùi d√πng ({users.length})</h3>
        <div className="badge">T·ªïng {users.length}</div>
      </div>

      <table className="table">
        <thead>
          <tr>
            <th>T√™n</th>
            <th>Email</th>
            <th>Thao t√°c</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u) => (
            <tr key={u._id}>
              <td>{u.name}</td>
              <td>{u.email}</td>
              <td>
