import React, { useState } from "react";
import { TextField, Button, Box, Alert } from "@mui/material";
import api from "../api";

export default function AddUser({ onAdded }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !email) {
      setError("Vui lòng nhập đầy đủ thông tin!");
      return;
    }
    try {
      await api.post("/users", { name, email });
      setName("");
      setEmail("");
      setError("");
      if (onAdded) onAdded();
    } catch (err) {
      setError("Không thể thêm user!");
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ mb: 3, display: "flex", gap: 2 }}>
      <TextField label="Tên" variant="outlined" size="small" value={name} onChange={(e) => setName(e.target.value)} />
      <TextField label="Email" variant="outlined" size="small" value={email} onChange={(e) => setEmail(e.target.value)} />
      <Button type="submit" variant="contained" color="primary">
        Thêm
      </Button>
      {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
    </Box>
  );
}
